cin &gt;&gt; a &gt;&gt; m &gt;&gt; l &gt;&gt; r;
a %= m;
if(a &lt; 0)
a += m;
LL k1 = l / m, r1 = l % m;
if(r1 &lt; 0)
{
r1 += m;
k1--;
}
if(r1 &gt; a)
k1++;
LL k2 = r / m, r2 = r % m;
if(r2 &lt; 0)
{
r2 += m;
k2--;
}
if(r2 &lt; a)
k2--;
cout &lt;&lt; k2 - k1 + 1 &lt;&lt; endl;
return 0;
}
