for(int i = 1; i &lt;= N; i++)
x[idx-i] = i;
int op, p;
char C[5];
while(Q--)
{
scanf("%d", &amp;op);
if(op == 1)
{
scanf("%s", C);
x[idx] = x[idx-1];
y[idx] = y[idx-1];
if(C[0] == 'U')
y[idx]++;
else if(C[0] == 'D')
y[idx]--;
else if(C[0] == 'L')
x[idx]--;
else
x[idx]++;
idx++;
}
else
{
scanf("%d", &amp;p);
printf("%d %d\n", x[idx-p], y[idx-p]);
}
}
return 0;
}
