for (int i = 0; i &lt; m; i++) {
eu[i] = in.nextInt();
ev[i] = in.nextInt();
if (a[eu[i]] == a[ev[i]])
merge(eu[i], ev[i]);
}
graph_init(n + 1, m + 1);
int[] ind = new int[n + 1];
for (int i = 0; i &lt; m; i++) {
if (a[eu[i]] != a[ev[i]]) {
int u = find(eu[i]);
int v = find(ev[i]);
if (a[u] &gt; a[v]) {
int t = u;
u = v;
v = t;
}
ind[v]++;
add(u, v);
}
}
