Queue&lt;Integer&gt; q = new ArrayDeque&lt;&gt;();
for (int i = 1; i &lt;= n; i++) {
if (i != find(i))
continue;
dp[find(i)] = (int) -1E6;
if (ind[find(i)] == 0)
q.add(i);
}
dp[find(1)] = 1;
while (!q.isEmpty()) {
