in.next().getChars(0, n, s, 1);

int op, l, r;
build(1, 0, n);
while (q-- &gt; 0) {
op = in.nextInt();
l = in.nextInt();
r = in.nextInt();
if (op == 1) {
update(1, 0, n, l - 1);
update(1, 0, n, r);
