for(int i = 2; i * i &lt;= x; i++)
{
if(x % i == 0)
{
int c = 0;
while(x % i == 0)
{
x /= i;
c ^= 1;
}
if(c)
ans *= i;
}
}
if(x != 1)
ans *= x;
