int n, T;
unordered_map&lt;LL, int&gt; cnt;
LL A[maxn];

int main()
{
cin &gt;&gt; n &gt;&gt; T;
cnt[0] = n;
while(T--)
{
int i, x;
scanf("%d %d", &amp;i, &amp;x);
if(--cnt[A[i]] == 0)
cnt.erase(A[i]);
A[i] += x;
if(cnt.count(A[i]) == 0)
cnt[A[i]] = 1;
else
cnt[A[i]]++;
printf("%ld\n", cnt.size());
}
return 0;
}
