Line(int x) : l(x), r(x + 7) {}
Line(int x, int y) : l(x), r(y) {}
int length() const {return r - l;}
};
Line merge(const Line &amp;x, const Line &amp;y)
{
if(x.l &gt; y.l)
return merge(y, x);
return Line(min(x.r, y.l), min(x.r, y.r));
