题目大意 <p>给你 \(N(N \le 2 \times 10^5)\) 个数，第 \(i\) 个数是 \(A_i\)，有 \(Q(Q \le 2 \times 10^5)\) 个操作，操作有两种，第一种操作是单点修改，第二种操作是询问一个区间内第二大的数字出现的次数。</p> 解题思路 <p>如果会用线段树维护区间次大值的话，这道题只需要再加上出现次数就好。（实际就是模板提，私以为这道题比上一题简单。）</p> 参考代码 C++Java <pre><code>#include &lt;iostream&gt;
#include &lt;cstdio&gt;
#include &lt;map&gt;

using namespace std;

const int maxn = 2E5 + 10;
int n, a[maxn];

struct Node {
int num[2], cnt[2];
Node() {num[0] = num[1] = cnt[0] = cnt[1] = 0;}
}t[maxn * 4];

Node pushup(const Node &amp;x, const Node &amp;y)
{
map&lt;int, int&gt; mp;
mp[x.num[0]] += x.cnt[0];
mp[x.num[1]] += x.cnt[1];
mp[y.num[0]] += y.cnt[0];
mp[y.num[1]] += y.cnt[1];
auto it = mp.rbegin();
Node res;
res.num[0] = it -&gt; first;
res.cnt[0] = it -&gt; second;
it++;
res.num[1] = it -&gt; first;
res.cnt[1] = it -&gt; second;
return res;
}

void build(int p, int beg, int end)
{
if(beg == end)
{
t[p].num[0] = a[beg];
t[p].cnt[0] = 1;
return;
}
int mid = (beg + end) / 2;
int lch = p * 2, rch = 2 * p + 1;
build(lch, beg, mid);
build(rch, mid+1, end);
t[p] = pushup(t[lch], t[rch]);
}

void update(int p, int beg, int end, int pos, int k)
{
if(beg == end)
{
t[p].num[0] = k;
return;
}
int mid = (beg + end) / 2;
int lch = p * 2, rch = 2 * p + 1;
pos &lt;= mid ? update(lch, beg, mid, pos, k) : update(rch, mid+1, end, pos, k);
t[p] = pushup(t[lch], t[rch]);
}

Node ask(int p, int beg, int end, int l, int r)
{
if(end &lt; l || beg &gt; r)
return Node();
if(beg &gt;= l &amp;&amp; end &lt;= r)
return t[p];
int mid = (beg + end) / 2;
int lch = p * 2, rch = 2 * p + 1;
return pushup(ask(lch, beg, mid, l, r), ask(rch, mid+1, end, l, r));
}

int main()
{
int t;
cin &gt;&gt; n &gt;&gt; t;
for(int i = 1; i &lt;= n; i++)
scanf("%d", &amp;a[i]);
build(1, 1, n);
while(t--)
{
int op, p, x, l, r;
scanf("%d", &amp;op);
if(op == 1)
{
scanf("%d %d", &amp;p, &amp;x);
update(1, 1, n, p, x);
}
else
{
scanf("%d %d", &amp;l, &amp;r);
printf("%d\n", ask(1, 1, n, l, r).cnt[1]);
}
}
return 0;
}
