var it = mp.entrySet().iterator();
Node res = new Node();
var entry = it.next();
res.num[0] = entry.getKey();
res.cnt[0] = entry.getValue();
entry = it.next();
res.num[1] = entry.getKey();
res.cnt[1] = entry.getValue();
return res;
}

