int main()
{
int a, b, c;
cin &gt;&gt; a &gt;&gt; b &gt;&gt; c;
int ba = (a - b + 24) % 24;
int bc = (c - b + 24) % 24;
cout &lt;&lt; (ba &lt;= bc ? "No" : "Yes") &lt;&lt; endl;
