cin &gt;&gt; n;
vector&lt;vector&lt;int&gt;&gt; a(n+1, vector&lt;int&gt;(n+1));
for(int i = 1; i &lt;= n; i++)
for(int j = 1; j &lt;= i; j++)
cin &gt;&gt; a[i][j];
