cin &gt;&gt; a[i];
int len = 1 &lt;&lt; n;
LL sum = accumulate(a.begin(), a.end(), 0);
LL ans = sum;
for(int s = 0; s &lt; len; s++)
{
LL x = 0;
for(int i = 0; i &lt; n; i++)
if(s &gt;&gt; i &amp; 1)
