string s;
cin &gt;&gt; s;
cout &lt;&lt; s[1] &lt;&lt; s[2] &lt;&lt; s[0] &lt;&lt; ' ' &lt;&lt; s[2] &lt;&lt; s[0] &lt;&lt; s[1] &lt;&lt; endl;
return 0;
