{
string s;
cin &gt;&gt; s;
int n = s.size();
int cnt = 0;
for(int i = 1; i &lt; n; i++)
{
if(s[i] == '|')
{
cout &lt;&lt; cnt &lt;&lt; ' ';
cnt = 0;
